    /// <summary>
    /// The sequence-number used to detect the order in which packages where sent.
    /// </summary>
    private byte _sequenceNumber;

    #endregion

    #region Constructors

    /// <inheritdoc />
    /// <summary>
    /// Initializes a new instance of the <see cref="T:RGB.NET.Devices.DMX.E131.E131UpdateQueue" /> class.
    /// </summary>
    /// <param name="updateTrigger">The update trigger used by this queue.</param>
    /// <param name="hostname">The hostname of the device this queue is performing updates for.</param>
    /// <param name="port">The port to connect to the device this queue is performing updates for.</param>
    public E131UpdateQueue(IDeviceUpdateTrigger updateTrigger, string hostname, int port)
        : base(updateTrigger)
    {
        _socket = new UdpClient();
        _socket.Connect(hostname, port);
    }

    #endregion

    #region Methods

    protected override void OnUpdate(object? sender, CustomUpdateData customData)
    {
        if (customData[CustomUpdateDataIndex.HEARTBEAT] as bool? ?? false)
            Update(Array.Empty<(object key, Color color)>());
        else
            base.OnUpdate(sender, customData);
    }

    /// <inheritdoc />
    protected override bool Update(ReadOnlySpan<(object key, Color color)> dataSet)
    {
        try
        {
            DataPacket.SetSequenceNumber(GetNextSequenceNumber());

            foreach ((object key, Color color) in dataSet)
            {
                LedChannelMapping mapping = (LedChannelMapping)key;
                foreach ((int channel, Func<Color, byte> getValue) in mapping)
                    DataPacket.SetChannel(channel, getValue(color));
            }

            _socket.Send(DataPacket, DataPacket.Length);

            return true;
        }
        catch (Exception ex)
        {
            DMXDeviceProvider.Instance.Throw(ex);
        }

        return false;
    }

    /// <summary>
    /// Increments the sequence number and wraps it if neded.
    /// </summary>
    /// <returns>The next usable sequence number.</returns>
    private byte GetNextSequenceNumber()
    {
        if (_sequenceNumber == byte.MaxValue)
        {
            _sequenceNumber = byte.MinValue;
            return byte.MaxValue;
        }

        return _sequenceNumber++;
    }

    public override void Dispose()
    {
        base.Dispose();

        _socket.Dispose();
    }

    #endregion
}